mapper.select:
		select B.* from(select rownum as rn, A.* from
		(select * from menu_info where value like #{value} and menuSize like #{size})A)B
        where rn between #{start} and #{end}

		select B.* from(select rownum as rn, A.* from
		(select * from menu_info where value like #{value} and menuSize like #{size})A)B
        where menuSeq=#{seq}

	select B.* from(select rownum as rn, A.* from
		(select * from menu_info where value like #{value} and menuSize like #{size})A)B
        where rn=#{page}
